var words = ["GRAPHIC DESIGNER", "WEB DEVELOPER", "CONTENT CREATOR", "WEB DESIGNER", "NERD", "TENACIOUS", "PROBLEM SOLVER", "MORALE BUILDER", "FUNNY SOMETIMES", "DREAMER", "LEARNING SPONGE"];
var i = 0;
var text = "This text changes";

function _getChangedText() {
  i = (i + 1) % words.length;
  return text.replace(/This text changes/, words[i]);
}

function _changeText() {
  var txt = _getChangedText();
  document.getElementById("rotatingDescriptors").innerHTML = txt;
}
setInterval("_changeText()", 1800);
